import React, { Component, Fragment } from "react";
import { Nav, Navbar } from "react-bootstrap";
import "../../asset/css/custom.css";
import "../../asset/css/bootstrap.min.css";
import logoBefore from "../../asset/image/log.png";
import logoAfter from "../../asset/image/logaf.jpg";
import { NavLink } from "react-router-dom";

class TopNavigation extends Component {
  constructor() {
    super();
    this.state = {
      navBarTitle: "navTitle",
      logo: [logoBefore],
      background: "navBackground",
      navVariant: "dark",
      navBarItem: "navItem"
    };
  }
  onScroll = () => {
    if (window.scrollY > 100) {
      this.setState({
        navBarTitle: "navTitleScroll",
        logo: [logoAfter],
        background: "navBackgroundScroll",
        navBarItem: "navItemScroll",
        navVariant: "light"
      });
    } else if (window.scrollY < 100) {
      this.setState({
        navBarTitle: "navTitle",
        logo: [logoBefore],
        background: "navBackground",
        navBarItem: "navItem",
        navVariant:"dark"
      });
    }
  };
  componentDidMount() {
    window.addEventListener("scroll", this.onScroll);
  }
  render() {
    return (
      <Fragment>
        <Navbar
          className={this.state.background}
          fixed="top"
          collapseOnSelect
          expand="lg"
          variant={this.state.navVariant}
        >
          <Navbar.Brand href="#home" className={this.state.navBarTitle}>
            <img
              src={this.state.logo}
              style={{ width: "20px" }}
              alt="TopNavigation"
            />{" "}
            First React
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="ml-auto">
              <NavLink to="/" className={this.state.navBarItem}>
                Home
              </NavLink>
              <NavLink to="service" className={this.state.navBarItem}>
                Services
              </NavLink>
              <NavLink to="course" className={this.state.navBarItem}>
                Cources
              </NavLink>
              <NavLink to="portfolio" className={this.state.navBarItem}>
                Portfolio
              </NavLink>
              <NavLink to="contact" className={this.state.navBarItem}>
                Contact
              </NavLink>
              <NavLink to="#pricing" className={this.state.navBarItem}>
                About
              </NavLink>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </Fragment>
    );
  }
}

export default TopNavigation;
