import React, { Component, Fragment } from "react";
import { Nav, Navbar } from "react-bootstrap";
import logoBefore from "../../asset/image/log.png";
import logoAfter from "../../asset/image/logaf.jng";

class TopNavigation extends Component {
  constructor() {
    super();
    this.state = {
      navBarTitle: "navTitle",
      logo: "logoBefore"
    };
  }
  onScroll=()=>{
      if(window.scrollY>100){
          this.setState({navBarTitle:'navTitleScroll', logo:''})
      }
      else if(window.scrollY<100){
        this.setState({navBarTitle:'navTitle'})
    }
  }
  componentDidMount(){
      window.addEventListener('scroll',this.onScroll)
  }
  render() {
    return (
      <Fragment>
        <Navbar
          fixed="top"
          collapseOnSelect
          expand="lg"
          bg="dark"
          variant="dark"
        >
          <Navbar.Brand href="#home" className={this.state.navBarTitle}>
            <img src={logoBefore} style={{ width:"20px" }} /> First React
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link href="#features">Home</Nav.Link>
              <Nav.Link href="#pricing">Services</Nav.Link>
              <Nav.Link href="#features">Cources</Nav.Link>
              <Nav.Link href="#pricing">Portfolio</Nav.Link>
              <Nav.Link href="#features">Contact</Nav.Link>
              <Nav.Link href="#pricing">About</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </Fragment>
    );
  }
}

export default TopNavigation;
